-- Creación de la tabla tmp_production_companies para almacenar los campos del JSON de production_companies.
DROP TABLE IF EXISTS tmp_production_companies ;
CREATE TABLE IF NOT EXISTS tmp_production_companies(
    idMovie INT,
    idCompany INT,
    name varchar(85)
);


-- Procedimiento para cargar la data en la tabla tmp_production_companies.
DROP PROCEDURE IF EXISTS table_tmp_production_companies;
DELIMITER //
CREATE PROCEDURE table_tmp_production_companies()
BEGIN
	DECLARE i INT DEFAULT 0;
    DECLARE max_j INT DEFAULT (
		SELECT DISTINCT MAX(JSON_LENGTH(production_companies))
		FROM movie_dataset_cleaned
	);
    -- Creación de la tabla temporal que va almacenar el tamaño de cada json
    CREATE TABLE IF NOT EXISTS max_json (id INT NOT NULL, max_tuple INT NOT NULL);
    INSERT INTO max_json (id, max_tuple) SELECT id,JSON_LENGTH(production_companies) FROM movie_dataset_cleaned;
    
	WHILE i < max_j DO
    -- Cargando datos del objeto JSON en la tabla temporal.
    INSERT INTO tmp_production_companies
    SELECT DISTINCT * FROM (
		SELECT id AS idMovie,
			JSON_EXTRACT(production_companies, CONCAT("$[",i,"].id")) AS idCompany,
			REPLACE(JSON_EXTRACT(production_companies, CONCAT("$[",i,"].name")), """", "") AS name
		FROM movie_dataset_cleaned
		WHERE id IN (SELECT id FROM max_json WHERE i < max_tuple)
	) t;
	SET i=i+1;
	END WHILE;
    -- Eliminación de la tabla temporal
    DROP TABLE IF EXISTS max_json;
	-- Limpieza de registros nulos en la tabla temporal production_companies.
	DELETE FROM tmp_production_companies
	WHERE idCompany IS NULL;
END //
DELIMITER ;

-- Llamada al procedimiento.
CALL table_tmp_production_companies();

-- Asignación de clave primaria en la tabla tmp_production_companies.
ALTER TABLE tmp_production_companies ADD PRIMARY KEY (idMovie, idCompany) ;
