-- Procedimiento para cargar la data en la tabla tmp_crew.
DROP PROCEDURE IF EXISTS table_tmp_production_countries;
DELIMITER //
CREATE PROCEDURE table_tmp_production_countries()
BEGIN
	DECLARE i INT DEFAULT 0;
    DECLARE max_j INT DEFAULT (
    -- Creación de la tabla temporal de los length
		SELECT DISTINCT MAX(JSON_LENGTH(production_countries))
		FROM movie_dataset_cleaned
	);
    CREATE TABLE IF NOT EXISTS max_json (id INT NOT NULL, max_tuple INT NOT NULL);
    INSERT INTO max_json (id, max_tuple) SELECT id, JSON_LENGTH(production_countries) FROM movie_dataset_cleaned;
	WHILE i < max_j DO
    -- Cargando datos del objeto JSON en la tabla temporal.
    INSERT INTO tmp_production_countries
    SELECT DISTINCT * FROM (
		SELECT id AS idMovie,
			REPLACE(JSON_EXTRACT(production_countries, CONCAT("$[",i,"].iso_3166_1")), """", "") AS iso31661,
			REPLACE(JSON_EXTRACT(production_countries, CONCAT("$[",i,"].name")), """", "") AS name
		FROM movie_dataset_cleaned
		WHERE id IN (SELECT id FROM max_json WHERE i < max_tuple)
	) t ;
	SET i=i+1;
	END WHILE;
    -- Limpieza de registros nulos en la tabla temporal production_countries.
	DELETE FROM tmp_production_countries
	WHERE iso31661 IS NULL;
    
    DROP TABLE IF EXISTS max_json;
END //
DELIMITER ;

