-- Procedimiento para cargar la data en la tabla tmp_spoken_languages.
DROP PROCEDURE IF EXISTS table_tmp_spoken_languages;
DELIMITER //
CREATE PROCEDURE table_tmp_spoken_languages()
BEGIN
	DECLARE i INT DEFAULT 0;
    CREATE TABLE IF NOT EXISTS max_json (id INT NOT NULL, max_tuple INT NOT NULL);
    INSERT INTO max_json (id, max_tuple) SELECT id, JSON_LENGTH(spoken_languages) FROM movie_dataset_cleaned;
	WHILE i < (
		SELECT DISTINCT MAX(JSON_LENGTH(spoken_languages))
		FROM movie_dataset_cleaned
    ) DO
    -- Cargando datos del objeto JSON en la tabla temporal.
    INSERT INTO tmp_spoken_languages
    SELECT DISTINCT * FROM (
		SELECT id AS idMovie,
			REPLACE(JSON_EXTRACT(spoken_languages, CONCAT("$[",i,"].iso_639_1")), """", "") AS iso6391,
			REPLACE(JSON_EXTRACT(spoken_languages, CONCAT("$[",i,"].name")), """", "") AS name
		FROM movie_dataset_cleaned
		WHERE id IN (SELECT id FROM max_json WHERE i < max_tuple)) t;
	SET i=i+1;
	END WHILE;
    -- Eliminación de la tabla temporal
    DROP TABLE IF EXISTS max_json;
    -- Limpieza de registros nulos en la tabla temporal spoken_languages.
	DELETE FROM tmp_spoken_languages
	WHERE iso6391 IS NULL;
END //
DELIMITER ;

