-- Creación de la tabla tmp_crew para almacenar los campos del JSON de crew.
DROP TABLE IF EXISTS tmp_crew;
CREATE TABLE IF NOT EXISTS tmp_crew (
    idMovie INT,
    idCrew INT,
    credit_id VARCHAR(25),
    job VARCHAR(75),
    department VARCHAR(25),
    gender INT,
    name VARCHAR(150)
);

-- Procedimiento para cargar la data en la tabla tmp_crew.
DROP PROCEDURE IF EXISTS table_tmp_crew;
DELIMITER //
CREATE PROCEDURE table_tmp_crew()
BEGIN
	DECLARE i INT DEFAULT 0 ;
    -- Creación de la tabla que va a albergar el tamaño de los json
    CREATE TABLE IF NOT EXISTS max_json (id INT NOT NULL, max_tuple INT NOT NULL);
    INSERT INTO max_json (id, max_tuple) SELECT id, JSON_LENGTH(crew) FROM movie_dataset_cleaned;
	WHILE i < (
		SELECT DISTINCT MAX(JSON_LENGTH(crew))
		FROM movie_dataset_cleaned
    ) DO
		-- Cargando datos del objeto JSON en la tabla temporal.
		INSERT INTO tmp_crew
        SELECT idMovie, idCrew, credit_id, job, department, gender,
        -- Limpieza de datos erroneos 
			IF(name LIKE '"''%' AND name LIKE '%''"',
				REPLACE(REPLACE(REPLACE(name, """'", """"),"'""", """"), """", ""),
				REPLACE(name, """", ""))
		FROM(
			SELECT id as idMovie, 
				JSON_EXTRACT(CONVERT(crew USING utf8mb4), CONCAT("$[",i,"].id")) AS idCrew,
				REPLACE(JSON_EXTRACT(CONVERT(crew USING utf8mb4), CONCAT("$[",i,"].credit_id")), 
                """", "") AS credit_id,
				REPLACE(JSON_EXTRACT(CONVERT(crew USING utf8mb4), CONCAT("$[",i,"].job")),
                """", "") AS job,
				REPLACE(JSON_EXTRACT(CONVERT(crew USING utf8mb4), CONCAT("$[",i,"].department")),
                """", "") AS department,
				JSON_EXTRACT(CONVERT(crew USING utf8mb4), CONCAT("$[",i,"].gender")) AS gender,
                -- Codificación de las letras no codificadas
				REPLACE(JSON_EXTRACT(CONVERT(REPLACE(crew, '\\\\', '\\') USING utf8mb4), 
                CONCAT("$[",i,"].name")), "\\t", "") AS name
			FROM movie_dataset_cleaned
			WHERE id IN (SELECT id FROM max_json WHERE i < max_tuple)) t; 
		SET i=i+1;	
	END WHILE;
    -- Eliminación de la tabla temporal
    DROP TABLE IF EXISTS max_json;
    -- Limpieza de registros nulos en la tabla temporal crew.
	DELETE FROM tmp_crew
	WHERE idCrew IS NULL;
END //
DELIMITER ;

-- Llamada al procedimiento table_tmp_crew.
CALL table_tmp_crew();

