    -- Procedimiento para cargar la data en la tabla tmp_cast.
DROP PROCEDURE IF EXISTS table_tmp_cast;
DELIMITER //
CREATE PROCEDURE table_tmp_cast()
BEGIN
	DECLARE i INT DEFAULT 0;
    DECLARE max_j INT DEFAULT 10;

	WHILE i < max_j DO
    -- Cargando datos del objeto JSON en la tabla temporal.

    INSERT INTO tmp_cast
    SELECT DISTINCT * FROM (
		SELECT id AS idMovie,
			REPLACE(JSON_EXTRACT(CastJSON, CONCAT("$[",i,"]")), """", "") AS name
		FROM (SELECT id, cast, SpacesNumber,
        REPLACE(
		CONCAT ('["',
	   			IF(SpacesNumber >= 13, CONCAT(SUBSTRING_INDEX(SUBSTRING_INDEX(cast, ' ', -14), ' ', 2), '","'), '') ,
	   			IF(SpacesNumber >= 11, CONCAT(SUBSTRING_INDEX(SUBSTRING_INDEX(cast, ' ', -12), ' ', 2), '","'), '') ,
	   			IF(SpacesNumber >= 9, CONCAT(SUBSTRING_INDEX(SUBSTRING_INDEX(cast, ' ', -10), ' ', 2), '","'), '') ,
	   			IF(SpacesNumber >= 7, CONCAT(SUBSTRING_INDEX(SUBSTRING_INDEX(cast, ' ', -8), ' ', 2), '","'), '') ,	
	   			IF(SpacesNumber >= 5, CONCAT(SUBSTRING_INDEX(SUBSTRING_INDEX(cast, ' ', -6), ' ', 2), '","'), '') ,
	   			IF(SpacesNumber >=3, CONCAT(SUBSTRING_INDEX(SUBSTRING_INDEX(cast, ' ', -4), ' ', 2), '","'), '') ,
        		IF(SpacesNumber >=1, CONCAT(SUBSTRING_INDEX(cast, ' ', -2), '"'), ''), 
        ']'), 
        "=", " ") AS CastJSON,
	LOCATE(" ", cast, LOCATE(" ", cast)+1),
	SUBSTRING(cast, 1, LOCATE(" ", cast, LOCATE(" ", cast)+1)),
	SUBSTRING(cast, LOCATE(" ", cast, LOCATE(" ", cast)+1)+1)	
FROM ( 
	SELECT id, cast, LENGTH(cast) - LENGTH(REPLACE(cast, ' ', '')) AS SpacesNumber FROM ( 
	SELECT id,		
    -- Limpieza y corrección de datos
	CONVERT(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE
	                                            (cast, ' Jr.', '=Jr.'), ' Jr ', '=Jr '), '. ', '.='),  ' The ', ' The='), ' the ', ' the='),
                                                'the=Cable Guy', 'the=Cable=Guy' ), 'Tupac Amaru Shakur', 'Tupac Amaru=Shakur') using utf8mb4) AS cast
	FROM   movie_dataset_cleaned WHERE Cast <> '') t1) t2 ) t3
	) t ;
	SET i=i+1;
	END WHILE;
    -- Limpieza de registros nulos en la tabla temporal production_countries.
	DELETE FROM tmp_cast
	WHERE name IS NULL;

END //
DELIMITER ;

